public class cadenasconcatenar {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String cad1 = "perico";
		String cad2 = "hola";
		System.out.println("la concatenación es " + cad1.concat(cad2));
		int longitud;
		longitud=(cad1.concat(cad2).length());
		System.out.println("la longitud de la cadena es "+longitud);
	}

}
